<html><head><style>div {display:inline-block;margin:20px;</style>
<script>
var counter=0;
var d = new Date();
var f = new Date();
function start(){
if (counter==0){
	d = new Date();
	counter=1;
}
else if (counter==37){
	counter++;
	f = new Date();
	alert("It took you "+(f-d)/1000+" seconds to click all "+counter+" tiles");
}
else {counter++;}
}


</script></head><body>

